package com.mzielinski.aoc2022.day06

import org.junit.jupiter.api.Assertions
import org.junit.jupiter.api.Test

internal class Day06Test {

    private val day06: Day06 = Day06()

    @Test
    fun part01() {
        Assertions.assertEquals(7, day06.tuningTrouble("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 4))
        Assertions.assertEquals(5, day06.tuningTrouble("bvwbjplbgvbhsrlpgdmjqwftvncz", 4))
        Assertions.assertEquals(6, day06.tuningTrouble("nppdvjthqldpwncqszvftbrmjlhg", 4))
        Assertions.assertEquals(10, day06.tuningTrouble("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 4))
        Assertions.assertEquals(11, day06.tuningTrouble("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 4))
        Assertions.assertEquals(1658, day06.tuningTrouble("", 4))
    }

    @Test
    fun part02() {
        Assertions.assertEquals(19, day06.tuningTrouble("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 14))
        Assertions.assertEquals(23, day06.tuningTrouble("bvwbjplbgvbhsrlpgdmjqwftvncz", 14))
        Assertions.assertEquals(23, day06.tuningTrouble("nppdvjthqldpwncqszvftbrmjlhg", 14))
        Assertions.assertEquals(29, day06.tuningTrouble("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 14))
        Assertions.assertEquals(26, day06.tuningTrouble("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 14))
        Assertions.assertEquals(2260, day06.tuningTrouble("", 14))
    }
}